mapper.insert:
INSERT INTO
		T_ACCOUNT
		(
			USER_ID
			, INV_DATE
			, NAME
			, AMOUNT
			, TAX
			, TOTAL
			, NOTE
			)
	VALUES
		(
			#{user_id}
			, #{inv_date}
			, #{name}
			, #{amount}
			, #{tax}
			, #{total}
			, #{note}
		)

mapper.update:
	UPDATE 
			T_ACCOUNT 
	   SET
		(
			INV_DATE = #{inv_date}
			, NAME = #{name}
			, AMOUNT = #{amount}
			, TAX = #{tax}
			, TOTAL = #{total}
			, NOTE = #{note}
		)
	WHERE
			USER_ID = #{user_id}
